sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
   var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;
  var spoke_connection = inputs.spoke_connection;
  var catalog_item = inputs.catalog_item;
  if(catalog_item != null && catalog_item != ""){
  sn_flow.FlowTemplates.updateFlowActions(newFlow,'1', 'template_catalog_item', catalog_item);
  }
  if(spoke_connection != null && spoke_connection != ""){
  new global.FlowTemplatesHelper().activateConnection(spoke_connection);
  }
})(inputs, outputs);

